package com.gao.service;import org.springframework.context.ApplicationContext;import org.springframework.context.support.FileSystemXmlApplicationContext;public class Entrance {	public static void main(String[] args) {		System.out.println("hello world");		String xmlPath = "//Users/lianggao/软件开发/001项目/spring-framework-5.2.0.RELEASE/01springdemo/src/main/resources/spring/spring-config.xml";		/**		 * 取到bean容器（由xml文件构建的）		 */		ApplicationContext applicationContext = new FileSystemXmlApplicationContext(xmlPath);		/**		 * 获取bean实例		 */		WelcomeService welcomeService = (WelcomeService) applicationContext.getBean("welcomeService");		welcomeService.sayHello("gaogao");	}}